using System;
using Newtonsoft.Json;

namespace TdLib
{
    /// <summary>
    /// Autogenerated TDLib APIs
    /// </summary>
    public partial class TdApi
    {
        /// <summary>
        /// Represents a remote file
        /// </summary>
        public class RemoteFile : Object
        {
            /// <summary>
            /// Data type for serialization
            /// </summary>
            [JsonProperty("@type")]
            public override string DataType { get; set; } = "remoteFile";

            /// <summary>
            /// Extra data attached to the message
            /// </summary>
            [JsonProperty("@extra")]
            public override string Extra { get; set; }

            /// <summary>
            /// Remote file identifier; may be empty. Can be used across application restarts or even from other devices for the current user. If the ID starts with "http://" or "https://", it represents the HTTP URL of the file. TDLib is currently unable to download files if only their URL is known.
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("id")]
            public string Id { get; set; }

            /// <summary>
            /// True, if the file is currently being uploaded (or a remote copy is being generated by some other means)
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("is_uploading_active")]
            public bool IsUploadingActive { get; set; }

            /// <summary>
            /// True, if a remote copy is fully available
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("is_uploading_completed")]
            public bool IsUploadingCompleted { get; set; }

            /// <summary>
            /// Size of the remote available part of the file; 0 if unknown
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("uploaded_size")]
            public int UploadedSize { get; set; }
        }
    }
}